\end{equation*}


\begin{enumerate}
  \setcounter{enumi}{7}
  \item If $x=f(t)$ and $y=g(t)$, then
\end{enumerate}


\begin{equation*}
\frac{d y}{d x}=\frac{d y / d t}{d x / d t}=\frac{g^{\prime}(t)}{f^{\prime}(t)} \tag{15}
\end{equation*}


